mapper.select:
select
  student_unit_id, student_id, unit_id, isFinished
from student_units


        and student_unit_id = #{studentUnits.studentUnitId}


        and student_id = #{studentUnits.studentId}


        and unit_id = #{studentUnits.unitId}


        and isFinished = #{studentUnits.isfinished}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from student_units


        and student_unit_id = #{studentUnitId}


        and student_id = #{studentId}


        and unit_id = #{unitId}


        and isFinished = #{isfinished}
