mapper.select:
select
  id, username, password, email, level, study_time
from test.student_user


        and id = #{id}


        and username = #{username}


        and password = #{password}


        and email = #{email}


        and level = #{level}


        and study_time = #{studyTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from test.student_user


        and id = #{id}


        and username = #{username}


        and password = #{password}


        and email = #{email}


        and level = #{level}


        and study_time = #{studyTime}

select username
from test.student_user
where username = #{username}

select email
from test.student_user
where email = #{email}
