mapper.select:
select
  unit_id, unit_name, subject_id, description, units_order, materials_path,text, text_description
from Units


        and unit_id = #{unitId}


        and unit_name = #{unitName}


        and subject_id = #{subjectId}


        and description = #{description}


          and units_order = #{units_order}


          and materials_path = #{materials_path}


          and text = #{text}


          and text_description = #{text_description}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from Units


        and unit_id = #{unitId}


        and unit_name = #{unitName}


        and subject_id = #{subjectId}


        and description = #{description}


          and units_order = #{units_order}


          and materials_path = #{materials_path}
