mapper.select:
select
  unit_id, unit_name, subject_id, description
from Units


        and unit_id = #{unitId}


        and unit_name = #{unitName}


        and subject_id = #{subjectId}


        and description = #{description}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from Units


        and unit_id = #{unitId}


        and unit_name = #{unitName}


        and subject_id = #{subjectId}


        and description = #{description}
